mapper.insert:
 INSERT INTO MEMBER_TB
         ( MEMBER_ID
         , MEMBER_NAME
         , PASSWORD
         , JOIN_DATE
 ) VALUES (
          #{member.memberId}
         ,#{member.memberName}
         ,#{member.password}
         ,now()
)

mapper.update:
UPDATE MEMBER_TB
SET   MEMBER_NAME = #{member.memberName}
    , PASSWORD = #{member.password}
WHERE MEMBER_ID = #{memberId}
